INSERT INTO faq (category, question, answer, sort_order, view_count, is_active, created_at, updated_at)
VALUES
    ('MEMBER_MEMBERSHIP', '[회원] 회원 등급은 어떻게 올라가나요?', '구매 누적 금액이 등급별 기준을 넘으면 익일 자동으로 상향됩니다.\n등급 산정은 매일 00시에 진행되며, 최근 12개월 간 결제 완료 금액을 기준으로 합니다.\n자세한 등급별 혜택은 마이페이지 > 멤버십 안내에서 확인해주세요.', 1, 0, TRUE, '2024-09-01 09:00:00', '2024-09-01 09:00:00'),
    ('ORDER_PAYMENT', '[결제] 결제 수단을 변경하고 싶어요.', '주문 완료 전이라면 주문서 페이지에서 원하는 결제 수단으로 변경할 수 있습니다.\n이미 결제가 완료된 경우 고객센터로 문의하거나 주문을 취소 후 재주문해주세요.', 2, 0, TRUE, '2024-09-01 09:05:00', '2024-09-01 09:05:00'),
    ('DELIVERY', '[배송] 배송은 얼마나 걸리나요?', '일반 배송은 결제 완료 후 평균 2~3일이 소요됩니다.\n오늘드림 상품은 지역에 따라 주문 후 최대 3시간 이내로 배송됩니다.\n배송 진행 상황은 마이페이지 > 주문내역에서 실시간 확인이 가능합니다.', 3, 0, TRUE, '2024-09-01 09:10:00', '2024-09-01 09:10:00'),
    ('CANCEL_EXCHANGE_REFUND', '[취소/교환] 주문을 취소하고 싶어요.', '마이페이지 > 주문내역에서 배송 준비 중 이전까지 직접 취소가 가능합니다.\n이미 배송 중인 경우에는 고객센터를 통해 반품 절차를 안내받으실 수 있습니다.', 4, 0, TRUE, '2024-09-01 09:15:00', '2024-09-01 09:15:00'),
    ('EVENT', '[이벤트] 이벤트 쿠폰은 어디에서 확인하나요?', '발급된 쿠폰은 마이페이지 > 쿠폰함에서 확인할 수 있습니다.\n유효 기간이 지난 쿠폰은 자동으로 소멸되니 사용 기한을 꼭 확인해주세요.', 5, 0, TRUE, '2024-09-01 09:20:00', '2024-09-01 09:20:00'),
    ('MEMBER_MEMBERSHIP', '[회원] 비회원도 주문이 가능한가요?', '비회원 주문은 가능하지만 일부 서비스 이용에 제한이 있습니다.\n주문 내역 확인과 사후 관리를 위해 회원 가입 후 이용하시길 권장드립니다.', 6, 0, TRUE, '2024-09-01 09:25:00', '2024-09-01 09:25:00'),
    ('ORDER_PAYMENT', '[결제] 주문 후 결제 금액이 두 번 결제되었어요.', '간편 결제 이용 시 일시적으로 이중 결제 내역이 보일 수 있으며, 1~3영업일 내 자동 정산됩니다.\n계속해서 중복 청구가 확인된다면 결제 영수증과 함께 고객센터로 문의해주세요.', 7, 0, TRUE, '2024-09-01 09:30:00', '2024-09-01 09:30:00'),
    ('DELIVERY', '[배송] 해외 배송도 가능한가요?', '현재 Persome은 국내 배송만 제공하고 있습니다.\n해외 배송 서비스가 추가될 경우 공지사항을 통해 가장 먼저 안내드릴 예정입니다.', 8, 0, TRUE, '2024-09-01 09:35:00', '2024-09-01 09:35:00'),
    ('CANCEL_EXCHANGE_REFUND', '[반품] 반품은 어떻게 신청하나요?', '상품 수령일로부터 7일 이내 마이페이지 > 주문내역에서 반품 신청이 가능합니다.\n사용 흔적이 있거나 포장이 훼손된 경우에는 반품이 어려울 수 있습니다.', 9, 0, TRUE, '2024-09-01 09:40:00', '2024-09-01 09:40:00'),
    ('EVENT', '[사은품] 사은품은 언제 받을 수 있나요?', '사은품이 포함된 주문은 상품과 함께 배송되며, 재고 상황에 따라 동일 가치의 대체품으로 발송될 수 있습니다.', 10, 0, TRUE, '2024-09-01 09:45:00', '2024-09-01 09:45:00'),
    ('DELIVERY', '[픽업] 픽업 서비스가 궁금해요.', '픽업 서비스는 온라인 주문 후 지정 매장에서 상품을 직접 수령하는 방식입니다.\n주문 단계에서 픽업 매장을 선택한 뒤, 준비 완료 알림을 받으면 매장을 방문해 주세요.', 11, 0, TRUE, '2024-09-01 09:50:00', '2024-09-01 09:50:00'),
    ('DELIVERY', '[오늘드림] 교환 상품도 매장에서 찾을 수 있나요?', '오늘드림 교환 상품은 지정된 픽업 매장에서만 수령 가능합니다.\n알림 문자에 안내된 매장으로 신분증과 주문번호를 지참하시고 방문해 주세요.', 12, 0, TRUE, '2024-09-01 09:55:00', '2024-09-01 09:55:00'),
    ('DELIVERY', '[오늘드림] 오늘드림 서비스가 궁금해요.', '오늘드림은 주문 후 최대 3시간 내 배송되는 빠른 배송 서비스입니다.\n주문 가능 시간과 서비스 지역은 장바구니 페이지에서 확인할 수 있습니다.', 13, 0, TRUE, '2024-09-01 10:00:00', '2024-09-01 10:00:00'),
    ('DELIVERY', '[오늘드림] 배송비는 얼마인가요?', '오늘드림 배송비는 기본 3,000원이며, 이벤트 또는 멤버십 등급에 따라 무료 배송 혜택이 적용될 수 있습니다.', 14, 0, TRUE, '2024-09-01 10:05:00', '2024-09-01 10:05:00'),
    ('CANCEL_EXCHANGE_REFUND', '[반품] 반품할 때 비용이 드나요?', '단순 변심으로 인한 반품은 왕복 배송비가 청구됩니다.\n상품 불량 또는 오배송의 경우 배송비는 Persome이 부담하며, 사진을 첨부해 고객센터로 접수해주세요.', 15, 0, TRUE, '2024-09-01 10:10:00', '2024-09-01 10:10:00'),
    ('ORDER_PAYMENT', '[취소] 주문한 상품의 옵션을 변경하고 싶어요.', '주문 상태가 "결제완료" 단계라면 고객센터로 변경 가능 여부를 확인해 주세요.\n배송 준비 중 이후에는 변경이 어려워 주문 취소 후 재주문을 권장드립니다.', 16, 0, TRUE, '2024-09-01 10:15:00', '2024-09-01 10:15:00'),
    ('DELIVERY', '[배송] 주문한 상품은 언제 배송되나요?', '주문서에서 안내된 출고 예정일 기준으로 1~2일 이내 배송됩니다.\n택배사 영업 상황에 따라 지연될 수 있으며, 지연 시 카카오 알림톡으로 별도 안내드립니다.', 17, 0, TRUE, '2024-09-01 10:20:00', '2024-09-01 10:20:00'),
    ('DELIVERY', '[배송] 일반배송으로 주문했는데, 오늘드림으로 받을 수도 있나요?', '재고 및 물류 센터 상황에 따라 일부 주문은 오늘드림으로 대체 배송될 수 있습니다.\n배송 유형이 변경되면 SMS 또는 알림톡으로 사전 안내드립니다.', 18, 0, TRUE, '2024-09-01 10:25:00', '2024-09-01 10:25:00'),
    ('DELIVERY', '[배송] 도서/산간 지역도 배송이 가능한가요?', '도서/산간 지역은 배송이 가능하며 추가 배송비가 발생할 수 있습니다.\n주문서 작성 시 배송 가능 여부와 추가 비용을 확인할 수 있습니다.', 19, 0, TRUE, '2024-09-01 10:30:00', '2024-09-01 10:30:00'),
    ('MEMBER_MEMBERSHIP', '[포인트] 마일리지는 언제 적립되나요?', '주문 상태가 "배송완료"로 변경된 익일 자동 적립됩니다.\n일부 협력사 상품은 정산 사정상 최대 7일이 소요될 수 있습니다.', 20, 0, TRUE, '2024-09-01 10:35:00', '2024-09-01 10:35:00');
INSERT INTO notice (title, content, is_important, view_count, is_active, created_at, updated_at, category)
VALUES
    ('[공지] Persome 개인정보 처리방침 개정 안내', 'Persome 개인정보 처리방침이 2024년 10월 1일부로 개정됩니다. 주요 변경 사항은 마이페이지 > 공지사항에서 확인하실 수 있습니다.', TRUE, 0, TRUE, '2024-08-28 10:00:00', '2024-08-28 10:00:00', 'NORMAL'),
    ('매장 픽업 서비스 일시 중단 안내', '시스템 점검으로 9월 15일 02:00~06:00 동안 매장 픽업 서비스가 일시 중단됩니다. 이용에 참고 부탁드립니다.', FALSE, 0, TRUE, '2024-08-25 09:00:00', '2024-08-25 09:00:00', 'MARKET'),
    ('배송 물량 증가에 따른 지연 안내', '추석 연휴 기간 물량 증가로 일부 지역의 배송이 최대 2일 지연될 수 있습니다. 고객 여러분의 양해 부탁드립니다.', FALSE, 0, TRUE, '2024-08-20 12:00:00', '2024-08-20 12:00:00', 'DELIVERY'),
    ('[이벤트] 9월 신규 회원 웰컴 쿠폰 지급', '9월 한 달간 신규 가입 고객님께 5천원 웰컴 쿠폰을 지급해 드립니다. 자세한 내용은 이벤트 페이지를 참고하세요.', FALSE, 0, TRUE, '2024-08-18 08:30:00', '2024-08-18 08:30:00', 'EVENT'),
    ('온라인 고객센터 시스템 점검 안내', '더 나은 서비스 제공을 위해 9월 1일 00:00~04:00 동안 고객센터 시스템 점검이 예정되어 있습니다.', FALSE, 0, TRUE, '2024-08-10 11:00:00', '2024-08-10 11:00:00', 'NORMAL');
